using UnityEngine;using System.Collections;// Inspectorで"Rigidbody"を追加してください。// Inspectorで"jumpPower"の数値をジャンプする高さを調整してください。// もしくは、変数を宣言してください。public class Jump : MonoBehaviour {    public float jumpPower;	// Use this for initialization	void Start () {        // jumpPower = 5;    }		// Update is called once per frame	void Update () {        if (Input.GetButtonDown("Jump"))        {            GetComponent<Rigidbody>().velocity = new Vector3(0, jumpPower, 0);        }		}}